import React, { useState, useRef, useEffect } from "react";
import { useLocation } from "react-router-dom"; 
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
} from "mdb-react-ui-kit";

import { uploadFiles, getUserImages } from "../api";

function Upload() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadedImages, setUploadedImages] = useState([]);
  const fileInputRef = useRef(null);
  const location = useLocation();
  const userId = location.state?.userId || 345;

  // Handle file selection
  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    setSelectedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("user_id", userId);

    selectedFiles.forEach((file) => {
      formData.append("file", file);
    });
    try {
      await uploadFiles(formData);
      fetchUploadedImages(userId);
    } catch (error) {
      console.error("Error uploading files:", error);
      alert("Failed to upload files. Please try again.");
    }
  };

  // Fetch images (GET request) based on user ID
  const fetchUploadedImages = async (userId) => {
    try {
      const response = await getUserImages(userId);
      setUploadedImages(response.data.images);
    } catch (error) {
      console.error("Error fetching images:", error);
      alert("Failed to fetch images. Please try again.");
    }
  };

  useEffect(() => {
    fetchUploadedImages(userId);
  }, [userId]);

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <MDBContainer fluid>
      <MDBRow className="d-flex justify-content-center align-items-center h-100" style={{ backgroundColor: "#f8f9fa" }}>
        <MDBCol col="12">
          <MDBCard
            className="bg-dark text-white my-5 mx-auto"
            style={{ borderRadius: "1rem", maxWidth: "800px", padding: "2rem" }} // Increased size and padding
          >
            <MDBCardBody className="d-flex flex-column align-items-center mx-auto w-100">
              <h3 className="text-center mb-4">File Upload</h3>
              <p className="text-white-50 text-center w-100 mb-4">
                Please select files to upload.
              </p>

              <form onSubmit={handleSubmit} className="w-100">
                <div className="mb-4 w-100 text-center">
                  <input
                    type="file"
                    ref={fileInputRef}
                    style={{ display: "none" }}
                    onChange={handleFileChange}
                    multiple
                  />
                  <MDBBtn
                    outline
                    color="light" // Changed button color
                    size="lg"
                    onClick={handleButtonClick}
                  >
                    Select Files
                  </MDBBtn>
                </div>

                <div className="w-100 text-center">
                  <MDBBtn outline color="light" size="lg" type="submit">
                    Upload
                  </MDBBtn>
                </div>
              </form>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
      <MDBRow className="d-flex justify-content-center">
        <MDBCol md="10">
          <MDBCard>
            <MDBCardBody>
              {uploadedImages.length > 0 && (
                <MDBRow className="mt-4">
                  {/* Scrollable container */}
                  <div
                    style={{
                      maxHeight: "400px", // Adjust the height as needed
                      overflowY: "auto", // Enable vertical scrolling
                    }}
                  >
                    <MDBRow>
                      {uploadedImages.map((imageUrl, index) => (
                        <MDBCol key={index} md="4" className="mb-4">
                          <MDBCard>
                            <MDBCardImage
                              src={imageUrl}
                              alt={`Uploaded by user ${userId}`}
                              position="top"
                              style={{ maxHeight: "200px", objectFit: "cover" }}
                            />
                          </MDBCard>
                        </MDBCol>
                      ))}
                    </MDBRow>
                  </div>
                </MDBRow>
              )}
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default Upload;
