import React, { useState, useRef, useEffect } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
} from "mdb-react-ui-kit";

// Import the API functions
import { uploadFiles, getUserImages } from '../api'; // Update the path as needed

function Upload() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadedImages, setUploadedImages] = useState([]);
  const fileInputRef = useRef(null);
  const userId = 345; // Example user ID, replace this with the actual value

  // Handle file selection
  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    setSelectedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  // Handle form submission for POST request
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (selectedFiles.length === 0) {
      alert("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("userId", userId);
    console.log("userid", userId);
    // Append selected files to the form data
    selectedFiles.forEach((file) => {
      formData.append("files", file);
    });

    try {
      // POST request using the uploadFiles function from api.js
      await uploadFiles(formData);

      alert("File(s) uploaded successfully!");

      // Fetch images associated with the user after successful upload
      fetchUploadedImages(userId);
    } catch (error) {
      console.error("Error uploading files:", error);
      alert("Failed to upload files. Please try again.");
    }
  };

  // Fetch images (GET request) based on user ID
  const fetchUploadedImages = async (userId) => {
    try {
      console.log("Fetching images for user:", userId);
      const response = await getUserImages(userId); // Passing the dynamic userId
      console.log("API response:", response.data);  // Check the structure of the response

      // Assuming response.data.images is the array of image URLs
      setUploadedImages(response.data.images); 
    } catch (error) {
      console.error("Error fetching images:", error);
      alert("Failed to fetch images. Please try again.");
    }
  };


// Usage example: assuming `userId` is dynamic
useEffect(() => {
  fetchUploadedImages(userId); // Dynamic user ID is passed here
}, [userId]);


  // Trigger the hidden file input
  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <MDBContainer fluid>
      <MDBRow className="d-flex justify-content-center align-items-center h-100">
        <MDBCol col="12">
          <MDBCard
            className="bg-dark text-white my-5 mx-auto"
            style={{ borderRadius: "1rem", maxWidth: "1400px" }}
          >
            <MDBCardBody className="p-5 d-flex flex-column align-items-center mx-auto w-100">
              <p className="text-white-50 text-center w-100 mb-4">
                Please select files to upload.
              </p>

              <form onSubmit={handleSubmit}>
                <div className="mb-4 w-100 text-center">
                  {/* Hidden file input */}
                  <input
                    type="file"
                    ref={fileInputRef}
                    style={{ display: "none" }}
                    onChange={handleFileChange}
                    multiple
                  />

                  {/* "+" Button to trigger file input */}
                  <MDBBtn
                    outline
                    color="white"
                    size="lg"
                    onClick={handleButtonClick}
                  >
                    +
                  </MDBBtn>
                </div>

                <div className="w-100 text-center">
                  <MDBBtn outline color="white" size="lg" type="submit">
                    Upload
                  </MDBBtn>
                </div>
              </form>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
      <MDBRow>

      </MDBRow>
    </MDBContainer>

  );
}

export default Upload;
