import React, { useState, useRef } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
} from "mdb-react-ui-kit";

function Upload() {
  const [selectedFile, setSelectedFile] = useState(null);
  const fileInputRef = useRef(null);

  // Handle file selection
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  // Handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    if (selectedFile) {
      alert(`File ${selectedFile.name} uploaded successfully!`);
    } else {
      alert("Please select a file to upload.");
    }
  };

  // Trigger the hidden file input
  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <MDBContainer fluid>
      <MDBRow className="d-flex justify-content-center align-items-center h-100">
        <MDBCol col="12">
          <MDBCard
            className="bg-dark text-white my-5 mx-auto"
            style={{ borderRadius: "1rem", maxWidth: "1400px" }}
          >
            <MDBCardBody className="p-5 d-flex flex-column align-items-center mx-auto w-100">
              <p className="text-white-50 text-center w-100 mb-4">
                Please select a file to upload.
              </p>

              <form onSubmit={handleSubmit}>
                <div className="mb-4 w-100 text-center">
                  {/* Hidden file input */}
                  <input
                    type="file"
                    ref={fileInputRef}
                    style={{ display: "none" }}
                    onChange={handleFileChange}
                  />

                  {/* "+" Button to trigger file input */}
                  <MDBBtn
                    outline
                    color="white"
                    size="lg"
                    onClick={handleButtonClick}
                  >
                    +
                  </MDBBtn>
                </div>

                <div className="w-100 text-center">
                  <MDBBtn outline color="white" size="lg" type="submit">
                    Upload
                  </MDBBtn>
                </div>
              </form>

              {selectedFile && (
                <div className="mt-4 text-center">
                  <p>File Name: {selectedFile.name}</p>
                  <p>File Size: {(selectedFile.size / 1024).toFixed(2)} KB</p>
                </div>
              )}
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default Upload;
