import React, { useState, useRef } from "react";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
} from "mdb-react-ui-kit";

function Upload() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const fileInputRef = useRef(null);

  // Handle file selection
  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    setSelectedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  // Handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    if (selectedFiles.length > 0) {
      alert(`${selectedFiles.length} file(s) uploaded successfully!`);
    } else {
      alert("Please select a file to upload.");
    }
  };

  // Trigger the hidden file input
  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  return (
    <MDBContainer fluid>
      <MDBRow className="d-flex justify-content-center align-items-center h-100">
        <MDBCol col="12">
          <MDBCard
            className="bg-dark text-white my-5 mx-auto"
            style={{ borderRadius: "1rem", maxWidth: "1400px" }}
          >
            <MDBCardBody className="p-5 d-flex flex-column align-items-center mx-auto w-100">
              <p className="text-white-50 text-center w-100 mb-4">
                Please select files to upload.
              </p>

              <form onSubmit={handleSubmit}>
                <div className="mb-4 w-100 text-center">
                  {/* Hidden file input */}
                  <input
                    type="file"
                    ref={fileInputRef}
                    style={{ display: "none" }}
                    onChange={handleFileChange}
                    multiple
                  />

                  {/* "+" Button to trigger file input */}
                  <MDBBtn
                    outline
                    color="white"
                    size="lg"
                    onClick={handleButtonClick}
                  >
                    +
                  </MDBBtn>
                </div>

                <div className="w-100 text-center">
                  <MDBBtn outline color="white" size="lg" type="submit">
                    Upload
                  </MDBBtn>
                </div>
              </form>

              {selectedFiles.length > 0 && (
                <MDBRow className="mt-4">
                  {selectedFiles.map((file, index) => (
                    <MDBCol key={index} md="4" className="mb-4">
                      <MDBCard>
                        <MDBCardImage
                          src={URL.createObjectURL(file)}
                          alt={file.name}
                          position="top"
                          style={{ maxHeight: "200px", objectFit: "cover" }}
                        />
                        <MDBCardBody>
                          <p>File Name: {file.name}</p>
                          <p>File Size: {(file.size / 1024).toFixed(2)} KB</p>
                        </MDBCardBody>
                      </MDBCard>
                    </MDBCol>
                  ))}
                </MDBRow>
              )}
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default Upload;
