import React, { useState, useRef, useEffect } from "react";
import { useLocation } from "react-router-dom";
import {
  MDBBtn,
  MDBContainer,
  MDBRow,
  MDBCol,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBCarousel,
} from "mdb-react-ui-kit";

import { uploadFiles, getUserImages } from "../api";

function Upload() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadedImages, setUploadedImages] = useState([]);
  const fileInputRef = useRef(null);
  const location = useLocation();
  const userId = location.state?.userId || 345;

  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    setSelectedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("user_id", userId);
    console.log("user id")

    selectedFiles.forEach((file) => {
      formData.append("file", file);
    });
    try {
      await uploadFiles(formData);
      fetchUploadedImages(userId);
    } catch (error) {
      console.error("Error uploading files:", error);
      alert("Failed to upload files. Please try again.");
    }
  };

  const fetchUploadedImages = async (userId) => {
    try {
      const response = await getUserImages(userId);
      setUploadedImages(response.data.images);
    } catch (error) {
      console.error("Error fetching images:", error);
      alert("Failed to fetch images. Please try again.");
    }
  };

  useEffect(() => {
    fetchUploadedImages(userId);
  }, [userId]);

  const handleButtonClick = () => {
    fileInputRef.current.click();
  };

  const handleGenerateSchedule = () => {
    alert("Schedule generated successfully!");
    // Add functionality here to handle schedule generation
  };

  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

  return (
    <MDBContainer fluid>
      <MDBRow className="d-flex justify-content-center align-items-center h-100">
        <MDBCol col="12">
          <MDBCard
            className="bg-dark text-white my-5 mx-auto"
            style={{ borderRadius: "1rem", maxWidth: "1400px" }}
          >
            <MDBCardBody className="p-5 d-flex flex-column align-items-center mx-auto w-100">
              <p className="text-white-50 text-center w-100 mb-4">
                Please select files to upload.
              </p>

              <form onSubmit={handleSubmit}>
                <div className="mb-4 w-100 text-center">
                  <input
                    type="file"
                    ref={fileInputRef}
                    style={{ display: "none" }}
                    onChange={handleFileChange}
                    multiple
                  />
                  <MDBBtn
                    outline
                    color="white"
                    size="lg"
                    onClick={handleButtonClick}
                  >
                    +
                  </MDBBtn>
                </div>

                <div className="w-100 text-center mb-3">
                  <MDBBtn outline color="white" size="lg" type="submit">
                    Upload
                  </MDBBtn>
                  <MDBBtn
                    outline
                    color="success"
                    size="lg"
                    className="ml-2"
                    onClick={handleGenerateSchedule}
                  >
                    Generate Schedule
                  </MDBBtn>
                </div>
              </form>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>

      {/* Scrollable days of the week */}
      <MDBRow>
        <MDBCard className="mb-4">
          <MDBCardBody>
            {uploadedImages.length > 0 && (
              <MDBRow className="mt-4">
                {/* Scrollable vertical container for days of the week */}
                <div
                  style={{
                    maxHeight: "500px", // Adjust as needed for vertical scrolling height
                    overflowY: "auto", // Enable vertical scrolling
                    padding: "10px",
                  }}
                >
                  {daysOfWeek.map((day, dayIndex) => (
                    <MDBCol key={dayIndex} className="mb-4">
                      <h5 className="text-center">{day}</h5>
                      {/* Horizontal scrollable container for images per day */}
                      <div
                        style={{
                          display: "flex",
                          flexWrap: "nowrap",
                          overflowX: "auto",
                          padding: "10px",
                        }}
                      >
                        {uploadedImages.map((imageUrl, index) => (
                          <MDBCard key={index} style={{ minWidth: "200px", marginRight: "10px" }}>
                            <MDBCardImage
                              src={imageUrl}
                              alt={`Uploaded by user ${userId}`}
                              style={{ height: "200px", objectFit: "cover" }}
                            />
                          </MDBCard>
                        ))}
                      </div>
                    </MDBCol>
                  ))}
                </div>
              </MDBRow>
            )}
          </MDBCardBody>
        </MDBCard>
      </MDBRow>
    </MDBContainer>
  );
}

export default Upload;
